import React, {Component} from 'react';

class App extends Component {
    constructor(props){
        super(props);
        this.state ={
            content: ''
        }

        this.getData = this.getData.bind(this);
        this.postData = this.postData.bind(this);
    }

    // get 讀取資料
    getData(){
        // fetch (url, {method:get..})
        fetch('http://localhost/read_data.php?f=1',{method:'get'})
        .then(res => res.json())
        .then(data => {
            console.log('data: ',data);
            if(data['status']==='succ'){
                this.setState({content:data['result']});
            }
            else {
                this.setState({content:data['ERROR']});
            }  
        })
        .catch(err=> {
            console.log('Err:',err);
        })
    }

    // post 讀取資料
    postData(){
        // fetch (url, {method:get..})
        fetch('http://localhost/read_data.php',{
            method:'post',
            header:{
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({a:100, b:200})
        })
        .then(res => res.json())
        .then(data => {
            console.log('data:',data);
            if(data['status']==='succ'){
                this.setState({content:data['result']});
            } else {
                this.setState({content:data['ERROR']});
            }  
        })
        .catch(err=> {
            console.log('Err:',err);
        })
    }

    render(){
        return (
            <div>
                <button onClick={this.getData}>Get Data</button>
                <button onClick={this.postData}>Post Data</button>
                <p>{this.state.content}</p>
            </div>

        )
    }
}

export default App;
