import React, { useState } from 'react'; // React 一定要的

// React component
function Progress(){
    console.log('Functional Component!')
    // const [狀態變數名稱, 改變狀態的函數名稱]
    // = useState(狀態初始值);
    const [barWidth,setWidth] = useState(0);
    return (
        <div style={{
            border:1,
            color:'black'
            }}>
            {/* 顯示文字 */}
            <p className="progress-bar"
                style={{
                    fontSize:70,
                    marginTop:0,
                    marginBottom:0,
                    paddingLeft:60,
                    color: 'brown'
                }}>1
            </p>

            {/* +1 */}
            <button 
            style={{
                padding:5,
                margin:10,
            }} 
            onClick={()=> {
            if(barWidth>=100){
                setWidth(0)
            } else{
                setWidth(barWidth+10)
            }
            }}>+1</button>

            {/* +1 */}
            <button 
            style={{
                padding:5,
                margin:10,
            }} 
            onClick={()=> {
            if(barWidth>=100){
                setWidth(0)
            } else{
                setWidth(barWidth+10)
            }
            }}>-1</button>

            {/* 全部歸零 */}
            <button 
            style={{
                padding:5,
                margin:10,
            }} 
            onClick={()=> {
            if(barWidth>=100){
                setWidth(0)
            } else{
                setWidth(barWidth+10)
            }
            }}>全部歸零</button>
        </div>
    )
}

export default Progress;
