import React, {Component} from 'react';
import Brother from './Brother';
import Sister from './Sister';

class Parent extends Component{
    constructor(props){
        super(props);
        this.state={
            moneyForBrother:60,
            moneyForSister:40
        }

        this.allocateMoney=this.allocateMoney.bind(this);
    }

    allocateMoney(target,amount){
        if(target==='brother'){
            this.setState({moneyForBrother:amount});
        } else{
            this.setState({moneyForSister:amount});
        }
    }

    render(){
        return (
            <div>
                <Brother money={this.state.moneyForBrother} aruge={this.allocateMoney}></Brother>
                <Sister money={this.state.moneyForSister} aruge={this.allocateMoney}></Sister>
            </div>
        )
    }
}

export default Parent;
