import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App2';
import reportWebVitals from './reportWebVitals';
import Progress from './Progress.js' // import 自建之 module

//React component
let isClick = false;
const printMessage = () => {
    isClick=!isClick;
    if(isClick){
        document.getElementById('show-area').innerHTML = '我被按到了！';
    } else{
        document.getElementById('show-area').innerHTML = '';
    }

}


ReactDOM.render( 
    <div>
        <App name={'** 我的名字'} age={20} handleClick={printMessage}>
            <h1>在 index.js 設定文字</h1>
        </App>
        <div id='show-area'></div>
        <Progress></Progress>
    </div>,
    document.getElementById('root')
);

// If you want to start measuring performance in your app, pass a function
