        super(props);
        this.state={
            barWidth:'30%'
        }

        this.flag = true;
        this.changePercent = this.changePercent.bind(this);
    }

    changePercent(){
        this.flag =! this.flag;
        if(this.flag){
            this.setState({barWidth:'30%'});
        }
        else{
            this.setState({barWidth:'70%'});
        }
    }

