import React from 'react';
import { withStyles, makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import Button from '@material-ui/core/Button';
import Userform from './InfoForm';

const StyledTableCell = withStyles((theme) => ({
  head: {
    backgroundColor: 'gray',
    color: theme.palette.common.white,
  },
  body: {
    fontSize: 14,
  },
}))(TableCell);

const StyledTableRow = withStyles((theme) => ({
  root: {
    '&:nth-of-type(odd)': {
      backgroundColor: theme.palette.action.hover,
    },
  },
}))(TableRow);

function createData(name, gender, phone, email) {
    // 回傳成物件
    return { name, gender, phone, email };
}


const useStyles = makeStyles({
  table: {
    minWidth: 700,
  },
});

export default function CustomizedTables() {
  const classes = useStyles();
  const [rows, setRows] = React.useState([]);
  const [openAdd, setopenAdd] = React.useStyles(false);

  const addRecord = () => {
    setopenAdd(true);
    //   const list = rows;
    //   list.push(createData('Tom','M','0938450304','test1@gmail.com'));
    //   setRows(list.slice(0)); // slice(0) 全陣列的複製
    //   console.log('rows',rows); 
  }

  return (
    <div>
        <Button variant='contained'
            color='primary'
            onClick={addRecord}>
                新增聯絡人
        </Button>
        <Userform open={openAdd}></Userform>
        <TableContainer component={Paper}>
            <Table className={classes.table} aria-label="customized table">
                {/* 表頭 */}
                <TableHead>
                <TableRow>
                    <StyledTableCell>姓名</StyledTableCell>
                    <StyledTableCell align="right">性別</StyledTableCell>
                    <StyledTableCell align="right">電話 / 手機</StyledTableCell>
                    <StyledTableCell align="right">Email</StyledTableCell>
                    <StyledTableCell align="right">備註</StyledTableCell>
                </TableRow>
                </TableHead>

                <TableBody>
                {rows.map((row,index) => (
                    // () => return value
                    // 所以這下面都是 return 的元件
                    // 或是用 { } 裡頭加上 return ()...

                    // key={index} 為參考值，不會顯示
                    <StyledTableRow key={index}>
                    <StyledTableCell component="th"scope="row">
                        {row.name}
                    </StyledTableCell>
                    <StyledTableCell align="right">{row.gender}</StyledTableCell>
                    <StyledTableCell align="right">{row.phone}</StyledTableCell>
                    <StyledTableCell align="right">{row.email}</StyledTableCell>
                    <StyledTableCell align="right"></StyledTableCell>
                    </StyledTableRow>

                ))}
                </TableBody>
            </Table>
        </TableContainer>
    </div>
  );
}
