import logo from './logo.svg';
import {useState} from 'react';
import './App.css';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import MyTable from './MyTable';

function App() {
  const [content, setCnt] = useState('');
  const [flag, setFlag] = useState(true);
  const [buttonFace, setBtnFace] = useState('Click me!');
  const [text, setText] = useState('');
   const handleClick = (e) => {
    setCnt(text);
  }

  const handleChange = (e) => {
    setText(e.target.value);
  }
  console.log('App => content = ',content, ',flag = ',flag,',text = ',text);
  return (
    <div className="App">
      <TextField id="standard-basic" label="Task" value={text} onChange={handleChange} />
      <Button variant="outlined" onClick={handleClick}>{buttonFace}</Button>
      <p>{content}</p>
    </div>
  );
}

export default App;
