import logo from './logo.svg';
import {useState} from 'react';
import './App.css';
import Button from '@material-ui/core/Button'

function App() {
  const [content, setCnt] = useState('');
  const [flag, setFlag] = useState(true);
  const handleClick = () => {
    if(flag){
      setCnt('Hello World!');
    } else {
      setCnt('');
      flag =! flag;
    } 
  }
  console.log('App => content = ',content, ',flag = ',flag);
  return (
    <div className="App">
      <Button variant="contained" onClick={handleClick}>Hello!</Button>
      {content}
    </div>
  );
}

export default App;
