import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import Container from '@material-ui/core/Container';
import EmojiPeopleIcon from '@material-ui/icons/EmojiPeople';
import FastfoodIcon from '@material-ui/icons/Fastfood';
import LocationCityIcon from '@material-ui/icons/LocationCity';
import Button from '@material-ui/core/Button';

const useStyles = makeStyles(theme => ({
  root: {
    padding: theme.spacing(12, 4),
  },
  card: {
    height: '100%',
    width: '100%',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    border: '1px solid black',
    borderRadius: '5px',
    textAlign: 'center',
  },
  icon: {
    padding: theme.spacing(2, 0),
  },
  title: {
    padding: theme.spacing(2),
  },
  featureList: {
    padding: theme.spacing(2),
  },
}));

const content = {
    title: [
        'Explore Tokyo',
        'Explore Tokyo New',
        'Eat Delicious Food',
        'Visit the Best Spots'
    ],
    description: [
        'Discover Tokyo like you never have before.',
        'Discover Tokyo like you never have before.',
        'Find the best local restaurants and bars.',
        'Check out some of the less known locations and attractions for tourists.'
    ]
};

const App = () => {
  const classes = useStyles();
  const [mygrid, setMygrid] = React.useState([]);

  // 產生物件
  function createData(heading,content){
      return {heading,content};
  }

  const data = [
      createData('Heading','Content'),
      createData('Heading','Content'),
  ]

  const addGrid = () => {

  }


  return (
    <div>
        <Button variant="outlined" color="primary" onClick={addGrid}>新增</Button>
        <Container component="section" maxWidth="lg"  className={classes.root}>  
            <Grid container spacing={3} alignItems="stretch">
                {
                    data.map((grid) => (
                        <Grid item xs={12} sm={4}>
                            <div className={classes.card}>

                                {/* icon */}
                                <EmojiPeopleIcon
                                color="primary"
                                fontSize="large"
                                className={classes.icon}
                                />

                                {/* 大標 */}
                                <Typography variant="h5" component="h3" className={classes.title}>
                                {grid.heading}
                                </Typography>

                                {/* 文案 */}
                                <Typography className={classes.featureList}>
                                {grid.content}
                                </Typography>                  
                            </div>
                        </Grid>
                    ))
                }

            </Grid>
        </Container>
    </div>

  );
};

export default App;
