import React from 'react';
import Grid from '@material-ui/core/Grid';

// 隨機顏色編碼（16進位）
function randomColor() {
    const code = Math.floor(Math.random()*16777615).toString(16);
    return '#'+code;
}

function Grid1() {
    return (
        <div>
            <Grid container>
                <Grid item xs={6} style={{background:randomColor()}}>
                    This is Grid 1
                </Grid>
                <Grid item xs={6} style={{background:randomColor()}}>
                    This is Grid 2
                </Grid>
            </Grid>
        </div>
    )
}

function App() {
    return (
        <Grid1></Grid1>
    )
}

export default App;
