import {useState} from 'react';
import './App.css';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import MyTable from './MyTable';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';

function App() {
  const [content, setCnt] = useState('');
  const [buttonFace, setBtnFace] = useState('Click me!');
  const [text, setText] = useState('');
  const [task, setTask] = useState([]); // 存 Todolist 的工作項目
  const [done, setDone] = useState([]); // 儲存已做完項目

   const handleClick = (e) => {
    const list = task;
    list.push(text);
    setTask(list);
    setText('');
  }

  const handleChange = (e) => {
    setText(e.target.value);
  }

  const removeTask = (index) => {
    console.log('remove task',index);
    const list = task;
    const new_list = list.slice(0,index).concat(list.slice(index+1));
    setTask(new_list);
  }

  console.log('App => task = ',task);
  return (
    <div style={{width:'900px'}}>
      {/* <TextField id="standard-basic" label="Task" value={text} onChange={handleChange} />
      <Button variant="outlined" onClick={handleClick} style={{verticalAlign: 'bottom'}}>{buttonFace}</Button>
      <p>{content}</p>
      <MyTable taskList={task} handleClick={removeTask}/> */}

      <Grid container spacing={2}>
        <Grid item xs={12}>
          <TextField id='standard-basic' label='Task'
            value={text} onChange={handleChange}></TextField>
          <Button variant='outlined'
            onClick={handleClick}
            style={{verticalAlign:'bottom'}}>
              {buttonFace}
          </Button>
          <p>{content}</p>
        </Grid>

        <Grid item xs={6}>
          <MyTable taskList={task} handleClick={removeTask} title={'未完成工作項目'}></MyTable>
        </Grid>
        <Grid item xs={6}>
          <MyTable taskList={done} handleClick={removeTask} title={'已完成工作項目'}></MyTable>
        </Grid>
      </Grid>
    </div>
  );
}
export default App;
