  const [buttonFace, setBtnFace] = useState('Click me!');
  const [text, setText] = useState('');
  cosnt [task, setTask] = useState([]);

   const handleClick = (e) => {
    const list = task;
    list.push(text);
    setTask(list);
    setText('');
  }

  const handleChange = (e) => {
    setText(e.target.value);
  }

